import  React, {useEffect, useState} from "react";
import {prototype, String, array} from 'prop-types';
import {
    Card,
    CardHeader,
    CardBody,
    Table
  } from "reactstrap";
  
const TableComponent = ({tasks}) => {
    const TaskArray = []
    useEffect(() => {
      for (let key = 1; key < tasks.Count; key++) {
        if(tasks[key].game_id === 1)
          TaskArray.push({id: tasks[key].game_id, title: tasks[key].game_name, result: tasks[key].timer})
        else if(tasks[key].game_id === 2)
          TaskArray.push({id: tasks[key].game_id, title: tasks[key].game_name, result: tasks[key].game_result})
        else if(tasks[key].game_id === 3)
          TaskArray.push({id: tasks[key].game_id, title: tasks[key].game_name, result: (tasks[key].true_answers+"/"+tasks[key].total_answers)})
        else if(tasks[key].game_id === 4)
          TaskArray.push({id: tasks[key].game_id, title: tasks[key].game_name, result: tasks[key].timer})
        else if(tasks[key].game_id === 5)
          TaskArray.push({id: tasks[key].game_id, title: tasks[key].game_name, alt_title: tasks[key].climate, result: tasks[key].correct_answers+"/"+tasks[key].total_answers})
      }
      console.log(TaskArray)
    },[tasks]);
    return (
        <>
            <Card className="card-tasks">
              <CardHeader>
                <h6 className="title d-inline">Tasks</h6>
                <p className="card-category d-inline"> today</p>
              </CardHeader>
              <CardBody>
                <div className="table-full-width table-responsive">
                  <Table>
                    <tbody>
                    {
                      TaskArray.map((task,index) =>
                      <tr>
                        <td>
                          <p className="title">{task.title}</p>
                          <p className="text-muted">
                            {task.result}
                          </p>
                        </td>
                      </tr>
                      )
                    }
                    </tbody>
                  </Table>
                </div>
              </CardBody>
            </Card>
        </>
    );
}
TableComponent.propTypes = {
  tasks: array
  };
  
TableComponent.defaultProps = {
  tasks: []
};
export default TableComponent;
