import  React, {useEffect, useState} from "react";
import TableComponent from "../components/Table/TableComponent";
import BoxGraph from "../components/BoxGraph/BoxGraph";
import LineGraph from "../components/LineGraph/LineGraph";
// reactstrap components
import {
  Row,
  Col
} from "reactstrap";
function Dashboard() {
  
const [geoTask,setGeoTasks] = useState([])
const [mathTask,setMathTasks] = useState([])
const [animTask,setAnimTasks] = useState([])
const [langTask,setLangTasks] = useState([])
const [chemTask,setChemTasks] = useState([])

useEffect(() => {
  async function fetchData(){
    fetch('http://localhost:3001/getTasks').then(response => response.json())
    .then(data => {
      for (const key in data.tasks) {
        if(data.tasks[key].game_id === 1)
          setMathTasks({title: data.tasks[key].game_name, result: data.tasks[key].game_result})
        else if(data.tasks[key].game_id === 2)
          setAnimTasks({title: data.tasks[key].game_name, result: data.tasks[key].true_answers+"/"+data.tasks[key].total_answers})
        else if(data.tasks[key].game_id === 3)
          setGeoTasks({title: data.tasks[key].game_name, climate: data.tasks[key].climate, result: data.tasks[key].correct_answers+"/"+data.tasks[key].total_answers})
        else if(data.tasks[key].game_id === 4)
          setLangTasks({title: data.tasks[key].game_name, result: data.tasks[key].timer})
        else if(data.tasks[key].game_id === 5)
          setChemTasks({title: data.tasks[key].game_name, result: data.tasks[key].timer})
      }
    })
    .catch(error => {
      console.log("error "+error)});
  }
  fetchData()
},[]);
  return (
    <>
      <div className="content">
        <Row>
          <Col xs="12">
            <LineGraph/>
          </Col>
        </Row>
        <Row>
          <Col lg="4">
            <LineGraph/>
          </Col>
          <Col lg="4">
            <BoxGraph/>
          </Col>
          <Col lg="4">
            <LineGraph/>
          </Col>
        </Row>
        <Row>
          <Col lg="6" md="12">
            <TableComponent/>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default Dashboard;
